.chat.copilot .input .text.container .text.row {
    display: flex;
    flex-direction: row;
    align-items: flex-start;
    justify-content: flex-start;
    gap: 10px;
    width: 100%;
    border-radius: 7px;
}
 
.chat.copilot .input .text.container .text.row svg {
    width: 20px;
    height: 20px;
    margin-top: 7px;
    fill: #767676;
}

.chat.copilot .input .text.container.disabled .text.row svg {
    fill: #afa5a5;
}

