@media only screen and (max-width: 500px) {
    .signIn {
        border: none;
        box-shadow: unset;
        top: 48px;
        left: 0;
        transform: unset;
        width: 100vw;
        height: 100vh;
        overflow: auto;
        padding-bottom: 55px;
        border-radius: 0;
        border-top: 1px solid #EDEBE9;
    }

    .signInButton {
        margin-left: auto;
        margin-right: auto;
    }

    .signInModal .overlay,
    .signInModal ~ .wholePage .identity,
