.orAndFilterRadio {
    -webkit-appearance: none;
    appearance: none;
    background-color: #fff;
    margin: 0 8px 0 0;
    font: inherit;
    color: #323130;
    width: 20px;
    height: 20px;
    border: 1px solid #323130;
    border-radius: 50%;
    display: grid;
    place-content: center;
}

.orAndFilterRadio:checked {
    border: 1px solid #0078D4;
}

.orAndFilterLabel:hover > .orAndFilterRadio {
    border-width: 1.5px;
    border-color: #0078D4;
}

.orAndFilterRadio::before {
    content: "";
    width: 10px;
    height: 10px;
    border-radius: 50%;
    transform: scale(0);
    transition: 80ms transform ease-in;
    box-shadow: inset 10px 10px #0078D4;
  }

.orAndFilterRadio:checked::before {
    transform: scale(1);
}

